import {Component } from '@angular/core';
import {Todo, TodoService} from "./todo.service";
import {fromEvent, Observable} from "rxjs";
import { debounceTime, distinctUntilChanged, filter, map, switchMap } from "rxjs/operators";

@Component({
  selector: 'app-root',
  template: `
    <div class="title">
      <h1>
        A list of TODOs
      </h1>
    </div>
    <div class="list">
      <label for="search">Search...</label>
      <input id="search" type="text"/>
      <app-progress-bar id="progress-bar"></app-progress-bar>
      <app-todo-item *ngFor="let todo of todos$ | async" [item]="todo"></app-todo-item>
    </div>
  `,
  styleUrls: ['app.component.scss']
})

// <input id="search" type="text" [(ngModel)]="searchText" (keyup.enter)="searchTodos(searchText)"/>

export class AppComponent {

  readonly todos$: Observable<Todo[]>;
  tasks: Todo[] = [];

  constructor(todoService: TodoService) {
    this.todos$ = todoService.getAll();
    this.todos$.subscribe(data => this.tasks = data)
  }

  readonly inputSearch = document.getElementById('search') as HTMLInputElement;

  readonly filteredTasks = fromEvent(this.inputSearch, 'input').pipe(
    map(e => (e.target as HTMLInputElement).value),
    filter(text => text.length > 2),
    debounceTime(1),
    distinctUntilChanged(),
    switchMap(searchTerm => {
      return this.tasks.filter(todo => todo.task.search(new RegExp(searchTerm, 'i')));
    })
  );

  // searchTodos() {
  //   const inputEl = document.getElementById("search")!
  //   let searchText = fromEvent(inputEl, 'keydown').pipe( 
  //     map((e => e.target!.value) 
  //   );
  //   console.log("calledled")
  //   if (!searchText) {
  //     return;
  //   }
  //   else {
  //     let matched: any = this.tasks.filter(todo => todo.task.search(new RegExp(searchText, 'i')));
  //     return;
  //   }
  // }


  //Implement loading

  ngAfterViewInit() {
    this.filteredTasks.subscribe((data) => (this.todos$ as any) = data);
    let element = document.getElementById('progress-bar')
    // console.log(element)
    if(element != undefined){
      setTimeout(() => {
        element!.id = 'progress-bar-hidden'
      }, 2_000)   
    }
  }

}
