import {Component, ElementRef, ViewChild} from '@angular/core';
import {Todo, TodoService} from "./todo.service";
import {Observable} from "rxjs";

@Component({
  selector: 'app-root',
  template: `
    <div class="title">
      <h1>
        A list of TODOs
      </h1>
    </div>
    <div class="list">
      <label for="search">Search...</label>
      <input id="search" type="text">
      <app-progress-bar #progress-bar></app-progress-bar>
      <app-todo-item *ngFor="let todo of todos$ | async" [item]="todo"></app-todo-item>
    </div>
  `,
  styleUrls: ['app.component.scss']
})
export class AppComponent {

  readonly todos$: Observable<Todo[]>;
  @ViewChild('progress-bar', { static: false }) myDiv: ElementRef | undefined;

  constructor(todoService: TodoService) {
    this.todos$ = todoService.getAll();
  }
  remove_progress_bar(): Observable<void> {
    return new Observable<void>(observer => {
      setTimeout(() => {
        document.getElementById('progress-bar').className = 'progress-bar-hidden';
      }, 2_000)
    })
  }
}
